            228 => 
            array (
                'id' => 36729,
                'capitulo_id' => 1383,
                'pericopa_id' => 4882,
                'num_versiculo' => 6,
                'contenido' => 'Ahora bien, ni los discípulos ni la multitud habían llevado pan ni vino;',
                'referencia' => '3 Nefi 20:6',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:42',
                'updated_at' => '2024-03-12 15:25:42',
            ),
            229 => 
            array (
                'id' => 36730,
                'capitulo_id' => 1383,
                'pericopa_id' => 4882,
                'num_versiculo' => 7,
                'contenido' => 'pero verdaderamente les dio de comer pan y de beber vino también.',
