            array (
                'id' => 36787,
                'capitulo_id' => 1384,
                'pericopa_id' => 4019,
                'num_versiculo' => 18,
                'contenido' => 'y arrancaré tus bosques de entre ti, y asolaré tus ciudades.',
                'referencia' => '3 Nefi 21:18',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:42',
