            287 => 
            array (
                'id' => 36788,
                'capitulo_id' => 1384,
                'pericopa_id' => 4019,
                'num_versiculo' => 19,
                'contenido' => 'Y acontecerá que todas las mentiras, y falsedades, y envidias, y contiendas, y supercherías sacerdotales, y fornicaciones, serán extirpadas.',
                'referencia' => '3 Nefi 21:19',
