            array (
                'id' => 36811,
                'capitulo_id' => 1385,
                'pericopa_id' => 4891,
                'num_versiculo' => 13,
                'contenido' => 'Y todos tus hijos serán instruidos por el Señor; y grande será la paz de tus hijos.',
