            array (
                'id' => 36852,
                'capitulo_id' => 1388,
                'pericopa_id' => 4945,
                'num_versiculo' => 5,
                'contenido' => 'He aquí, yo os enviaré a Elías el profeta antes que venga el día grande y terrible del Señor;',
                'referencia' => '3 Nefi 25:5',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:42',
                'updated_at' => '2024-03-12 15:25:42',
