                'capitulo_id' => 1404,
                'pericopa_id' => 4083,
                'num_versiculo' => 7,
                'contenido' => 'Coriantor era hijo de Morón;',
                'referencia' => 'Eter 1:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:42',
                'updated_at' => '2024-03-12 15:25:42',
            ),
