            355 => 
            array (
                'id' => 37357,
                'capitulo_id' => 1409,
                'pericopa_id' => 4095,
                'num_versiculo' => 1,
                'contenido' => 'Y ahora yo, Moroni, procedo a dar la historia de Jared y su hermano.',
                'referencia' => 'Eter 6:1',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:42',
