            array (
                'id' => 37371,
                'capitulo_id' => 1409,
                'pericopa_id' => 4554,
                'num_versiculo' => 15,
                'contenido' => 'Y el hermano de Jared también engendró hijos e hijas.',
                'referencia' => 'Eter 6:15',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:42',
                'updated_at' => '2024-03-12 15:25:42',
