            array (
                'id' => 37834,
                'capitulo_id' => 1429,
                'pericopa_id' => 4251,
                'num_versiculo' => 3,
                'contenido' => 'Y los rebeldes serán traspasados de mucho pesar; porque se pregonarán sus iniquidades desde los techos de las casas, y sus hechos secretos serán revelados.',
                'referencia' => 'Doctrina y Convenios 1:3',
                'imagen' => '',
                'pie_imagen' => '',
