            array (
                'id' => 37846,
                'capitulo_id' => 1429,
                'pericopa_id' => 4533,
                'num_versiculo' => 15,
                'contenido' => 'porque se han desviado de mis ordenanzas y han violado mi convenio sempiterno.',
