            array (
                'id' => 37848,
                'capitulo_id' => 1429,
                'pericopa_id' => 4534,
                'num_versiculo' => 17,
                'contenido' => 'Por tanto, yo, el Señor, sabiendo las calamidades que sobrevendrían a los habitantes de la tierra, llamé a mi siervo José Smith, hijo, y le hablé desde los cielos y le di mandamientos;',
                'referencia' => 'Doctrina y Convenios 1:17',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:43',
                'updated_at' => '2024-03-12 15:25:43',
