            array (
                'id' => 37849,
                'capitulo_id' => 1429,
                'pericopa_id' => 4534,
                'num_versiculo' => 18,
                'contenido' => 'y también a otros di mandamientos de proclamar estas cosas al mundo; y todo esto para que se cumpliese lo que escribieron los profetas:',
                'referencia' => 'Doctrina y Convenios 1:18',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:43',
                'updated_at' => '2024-03-12 15:25:43',
