            array (
                'id' => 37850,
                'capitulo_id' => 1429,
                'pericopa_id' => 4534,
                'num_versiculo' => 19,
                'contenido' => 'Lo débil del mundo vendrá y abatirá lo fuerte y poderoso, para que el hombre no aconseje a su prójimo, ni ponga su confianza en el brazo de la carne;',
                'referencia' => 'Doctrina y Convenios 1:19',
