                'id' => 37851,
                'capitulo_id' => 1429,
                'pericopa_id' => 4534,
                'num_versiculo' => 20,
                'contenido' => 'sino que todo hombre hable en el nombre de Dios el Señor, el Salvador del mundo;',
                'referencia' => 'Doctrina y Convenios 1:20',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:43',
                'updated_at' => '2024-03-12 15:25:43',
