                'pericopa_id' => 4254,
                'num_versiculo' => 27,
                'contenido' => 'y para que cuando pecasen, fueran disciplinados para que se arrepintieran;',
                'referencia' => 'Doctrina y Convenios 1:27',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:43',
