            array (
                'id' => 38036,
                'capitulo_id' => 1438,
                'pericopa_id' => 4302,
                'num_versiculo' => 30,
                'contenido' => 'He aquí, te digo que no volverás a traducir aquellas palabras que han salido de tus manos;',
