            array (
                'id' => 38041,
                'capitulo_id' => 1438,
                'pericopa_id' => 4303,
                'num_versiculo' => 35,
                'contenido' => 'No te maravilles de que te dije: Aquí hay sabiduría, no lo muestres al mundo; pues yo dije que no lo muestres al mundo a fin de que seas preservado.',
                'referencia' => 'Doctrina y Convenios 10:35',
