                'id' => 38042,
                'capitulo_id' => 1438,
                'pericopa_id' => 4303,
                'num_versiculo' => 36,
                'contenido' => 'He aquí, no te digo que no lo muestres a los justos;',
                'referencia' => 'Doctrina y Convenios 10:36',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:43',
                'updated_at' => '2024-03-12 15:25:43',
