            41 => 
            array (
                'id' => 38043,
                'capitulo_id' => 1438,
                'pericopa_id' => 4303,
                'num_versiculo' => 37,
                'contenido' => 'pero como no siempre puedes juzgar quiénes son justos, o como no siempre puedes discernir a los malvados de los justos, es por lo que te digo que guardes silencio hasta que yo considere propio dar a conocer al mundo todas las cosas concernientes al asunto.',
                'referencia' => 'Doctrina y Convenios 10:37',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:43',
                'updated_at' => '2024-03-12 15:25:43',
