            180 => 
            array (
                'id' => 38199,
                'capitulo_id' => 1446,
                'pericopa_id' => 4331,
                'num_versiculo' => 34,
                'contenido' => 'Estas palabras no son de hombres, ni de hombre, sino mías; por tanto, testificaréis que son de mí, y no del hombre.',
                'referencia' => 'Doctrina y Convenios 18:34',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:43',
                'updated_at' => '2024-03-12 15:25:43',
