            187 => 
            array (
                'id' => 38208,
                'capitulo_id' => 1446,
                'pericopa_id' => 4333,
                'num_versiculo' => 41,
                'contenido' => 'Y habéis de predicar al mundo, diciendo: Debéis arrepentiros y bautizaros en el nombre de Jesucristo;',
                'referencia' => 'Doctrina y Convenios 18:41',
