            188 => 
            array (
                'id' => 38209,
                'capitulo_id' => 1446,
                'pericopa_id' => 4333,
                'num_versiculo' => 42,
                'contenido' => 'porque todos los hombres deben arrepentirse y bautizarse, y no únicamente los hombres, sino las mujeres y los niños que hayan llegado a la edad de responsabilidad.',
                'referencia' => 'Doctrina y Convenios 18:42',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:43',
                'updated_at' => '2024-03-12 15:25:43',
            ),
