            array (
                'id' => 38214,
                'capitulo_id' => 1446,
                'pericopa_id' => 4333,
                'num_versiculo' => 47,
                'contenido' => 'He aquí, yo, Jesucristo, vuestro Señor, Dios y Redentor, lo he hablado por el poder de mi Espíritu. Amén.',
                'referencia' => 'Doctrina y Convenios 18:47',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:43',
                'updated_at' => '2024-03-12 15:25:43',
            ),
            194 => 
            array (
                'id' => 38216,
                'capitulo_id' => 1447,
                'pericopa_id' => 4427,
                'num_versiculo' => 1,
                'contenido' => 'Yo soy el Alfa y la Omega, Cristo el Señor; sí, soy él, el principio y el fin, el Redentor del mundo.',
