            array (
                'id' => 38594,
                'capitulo_id' => 1462,
                'pericopa_id' => 4958,
                'num_versiculo' => 8,
                'contenido' => 'Y será un día grande al tiempo de mi venida, porque todas las naciones temblarán.',
