            array (
                'id' => 38606,
                'capitulo_id' => 1463,
                'pericopa_id' => 4961,
                'num_versiculo' => 5,
                'contenido' => 'Bautizaste en el agua para arrepentimiento, pero no recibieron el Espíritu Santo;',
                'referencia' => 'Doctrina y Convenios 35:5',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
