            array (
                'id' => 38612,
                'capitulo_id' => 1463,
                'pericopa_id' => 4962,
                'num_versiculo' => 10,
                'contenido' => 'Y pronto viene la hora en que se mostrarán grandes cosas a los hijos de los hombres;',
                'referencia' => 'Doctrina y Convenios 35:10',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
