            array (
                'id' => 38618,
                'capitulo_id' => 1463,
                'pericopa_id' => 4963,
                'num_versiculo' => 15,
                'contenido' => 'Y se predicará el evangelio a los pobres y a los mansos, y estarán esperando la hora de mi venida, porque ya está próxima;',
                'referencia' => 'Doctrina y Convenios 35:15',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
