            37 => 
            array (
                'id' => 38619,
                'capitulo_id' => 1463,
                'pericopa_id' => 4963,
                'num_versiculo' => 16,
                'contenido' => 'y aprenderán la parábola de la higuera, porque ahora mismo el verano se aproxima.',
                'referencia' => 'Doctrina y Convenios 35:16',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
