            40 => 
            array (
                'id' => 38623,
                'capitulo_id' => 1463,
                'pericopa_id' => 4964,
                'num_versiculo' => 19,
                'contenido' => 'Por tanto, vela por él para que su fe no falte, y se concederá por el Consolador, el Espíritu Santo, que sabe todas las cosas.',
                'referencia' => 'Doctrina y Convenios 35:19',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
