            array (
                'id' => 38626,
                'capitulo_id' => 1463,
                'pericopa_id' => 4965,
                'num_versiculo' => 21,
                'contenido' => 'porque oirán mi voz y me verán, y no estarán dormidos, y soportarán el día de mi venida; porque serán purificados, tal como yo soy puro.',
                'referencia' => 'Doctrina y Convenios 35:21',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
