            array (
                'id' => 38723,
                'capitulo_id' => 1467,
                'pericopa_id' => 4981,
                'num_versiculo' => 21,
                'contenido' => 'Porque el tiempo está cerca; ningún hombre sabe el día ni la hora; mas de cierto llegará.',
                'referencia' => 'Doctrina y Convenios 39:21',
