            125 => 
            array (
                'id' => 38726,
                'capitulo_id' => 1467,
                'pericopa_id' => 4982,
                'num_versiculo' => 23,
                'contenido' => 'Y además, sucederá que a cuantos bautices con agua, les impondrás las manos y recibirán el don del Espíritu Santo, y esperarán las señales de mi venida y me conocerán.',
                'referencia' => 'Doctrina y Convenios 39:23',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
            ),
            126 => 
            array (
                'id' => 38727,
                'capitulo_id' => 1467,
                'pericopa_id' => 4982,
                'num_versiculo' => 24,
                'contenido' => 'Y he aquí, vengo pronto. Así sea. Amén.',
                'referencia' => 'Doctrina y Convenios 39:24',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
