            array (
                'id' => 38764,
                'capitulo_id' => 1470,
                'pericopa_id' => NULL,
                'num_versiculo' => 15,
                'contenido' => 'Y todo esto procuraréis hacer como yo he mandado en cuanto a vuestras enseñanzas, hasta que se reciba la plenitud de mis Escrituras.',
                'referencia' => 'Doctrina y Convenios 42:15',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
            ),
            157 => 
            array (
                'id' => 38765,
                'capitulo_id' => 1470,
                'pericopa_id' => NULL,
                'num_versiculo' => 16,
                'contenido' => 'Y al elevar vuestras voces por medio del Consolador, hablaréis y profetizaréis conforme a lo que me parezca bien;',
                'referencia' => 'Doctrina y Convenios 42:16',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
            ),
            158 => 
            array (
                'id' => 38766,
                'capitulo_id' => 1470,
                'pericopa_id' => NULL,
                'num_versiculo' => 17,
                'contenido' => 'pues he aquí, el Consolador sabe todas las cosas, y da testimonio del Padre y del Hijo.',
                'referencia' => 'Doctrina y Convenios 42:17',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
