            array (
                'id' => 38927,
                'capitulo_id' => 1473,
                'pericopa_id' => 4193,
                'num_versiculo' => 24,
                'contenido' => 'Y esto os he dicho acerca de Jerusalén; y cuando venga ese día, será esparcido un resto entre todas las naciones;',
                'referencia' => 'Doctrina y Convenios 45:24',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
            ),
            300 => 
            array (
                'id' => 38928,
                'capitulo_id' => 1473,
                'pericopa_id' => 4193,
                'num_versiculo' => 25,
                'contenido' => 'mas serán recogidos de nuevo; pero quedarán hasta después del cumplimiento de los tiempos de los gentiles.',
                'referencia' => 'Doctrina y Convenios 45:25',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
