            441 => 
            array (
                'id' => 39091,
                'capitulo_id' => 1478,
                'pericopa_id' => 5031,
                'num_versiculo' => 20,
                'contenido' => 'Si es de alguna otra manera, no es de Dios.',
                'referencia' => 'Doctrina y Convenios 50:20',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
