            array (
                'id' => 39097,
                'capitulo_id' => 1478,
                'pericopa_id' => 5032,
                'num_versiculo' => 25,
                'contenido' => 'Y además, de cierto os digo, y lo digo para que sepáis la verdad, a fin de que desechéis las tinieblas de entre vosotros:',
                'referencia' => 'Doctrina y Convenios 50:25',
