            447 => 
            array (
                'id' => 39099,
                'capitulo_id' => 1478,
                'pericopa_id' => 5033,
                'num_versiculo' => 26,
                'contenido' => 'El que es ordenado por Dios y enviado, éste es nombrado para ser el mayor, a pesar de ser el menor y el siervo de todos.',
                'referencia' => 'Doctrina y Convenios 50:26',
                'imagen' => '',
