            array (
                'id' => 39101,
                'capitulo_id' => 1478,
                'pericopa_id' => 5033,
                'num_versiculo' => 28,
                'contenido' => 'Pero ningún hombre posee todas las cosas, a menos que sea purificado y limpiado de todo pecado.',
