            array (
                'id' => 39103,
                'capitulo_id' => 1478,
                'pericopa_id' => 5034,
                'num_versiculo' => 29,
                'contenido' => 'Y si sois purificados y limpiados de todo pecado, pediréis cuanto quisiereis en el nombre de Jesús y se cumplirá.',
                'referencia' => 'Doctrina y Convenios 50:29',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
