            454 => 
            array (
                'id' => 39107,
                'capitulo_id' => 1478,
                'pericopa_id' => 5034,
                'num_versiculo' => 33,
                'contenido' => 'no con acusaciones injuriosas, para que no seáis vencidos; ni con jactancia ni regocijo, para que no seáis asidos por él.',
                'referencia' => 'Doctrina y Convenios 50:33',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
