            455 => 
            array (
                'id' => 39108,
                'capitulo_id' => 1478,
                'pericopa_id' => 5034,
                'num_versiculo' => 34,
                'contenido' => 'Aquel que de Dios reciba, acredíteselo a Dios, y regocíjese de que Dios lo considere digno de recibir.',
                'referencia' => 'Doctrina y Convenios 50:34',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
