            458 => 
            array (
                'id' => 39112,
                'capitulo_id' => 1478,
                'pericopa_id' => 5035,
                'num_versiculo' => 37,
                'contenido' => 'Salgan mi siervo Joseph Wakefield, con quien estoy bien complacido, y mi siervo Parley P. Pratt, entre las iglesias para fortalecerlas con la palabra de exhortación;',
                'referencia' => 'Doctrina y Convenios 50:37',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:44',
                'updated_at' => '2024-03-12 15:25:44',
            ),
            459 => 
            array (
                'id' => 39113,
                'capitulo_id' => 1478,
                'pericopa_id' => 5035,
                'num_versiculo' => 38,
                'contenido' => 'y también mi siervo John Corrill, o cuantos de mis siervos sean ordenados a este oficio; y obren en la viña; y nadie les impida hacer lo que yo les he señalado.',
                'referencia' => 'Doctrina y Convenios 50:38',
