                'capitulo_id' => 1478,
                'pericopa_id' => 5035,
                'num_versiculo' => 46,
                'contenido' => 'Velad, pues, para que estéis apercibidos. Así sea. Amén.',
                'referencia' => 'Doctrina y Convenios 50:46',
                'imagen' => '',
                'pie_imagen' => '',
