            16 => 
            array (
                'id' => 39765,
                'capitulo_id' => 1503,
                'pericopa_id' => 5214,
                'num_versiculo' => 19,
                'contenido' => 'Y en cualquier casa donde entréis y os reciban, dejad allí vuestra bendición.',
                'referencia' => 'Doctrina y Convenios 75:19',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:45',
                'updated_at' => '2024-03-12 15:25:45',
