            array (
                'id' => 40152,
                'capitulo_id' => 1512,
                'pericopa_id' => 5062,
                'num_versiculo' => 95,
                'contenido' => '¡Ay, vuelvo a decir, de aquella casa, aldea o ciudad que os rechace a vosotros, o vuestras palabras, o vuestro testimonio de mí!',
                'referencia' => 'Doctrina y Convenios 84:95',
