            array (
                'id' => 40159,
                'capitulo_id' => 1512,
                'pericopa_id' => 5063,
                'num_versiculo' => 101,
                'contenido' => 'La tierra ha estado de parto y ha dado a luz su fuerza;',
                'referencia' => 'Doctrina y Convenios 84:101',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:45',
                'updated_at' => '2024-03-12 15:25:45',
