            373 => 
            array (
                'id' => 40425,
                'capitulo_id' => 1518,
                'pericopa_id' => 5202,
                'num_versiculo' => 21,
                'contenido' => 'Y permanezca mi consejero, a saber, Sidney Rigdon, donde ahora reside hasta que la boca del Señor lo diga.',
                'referencia' => 'Doctrina y Convenios 90:21',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:45',
                'updated_at' => '2024-03-12 15:25:45',
