            array (
                'id' => 40427,
                'capitulo_id' => 1518,
                'pericopa_id' => 5202,
                'num_versiculo' => 23,
                'contenido' => 'para que así pueda liquidar toda deuda; a fin de que el almacén del Señor no caiga en descrédito ante los ojos del pueblo.',
                'referencia' => 'Doctrina y Convenios 90:23',
