            463 => 
            array (
                'id' => 40531,
                'capitulo_id' => 1522,
                'pericopa_id' => 5283,
                'num_versiculo' => 13,
                'contenido' => 'Y en el tercer solar, mi siervo Hyrum Smith recibirá su heredad.',
                'referencia' => 'Doctrina y Convenios 94:13',
