            466 => 
            array (
                'id' => 40534,
                'capitulo_id' => 1522,
                'pericopa_id' => 5283,
                'num_versiculo' => 16,
                'contenido' => 'No se han de edificar estas dos casas hasta que os dé un mandamiento en cuanto a ellas.',
                'referencia' => 'Doctrina y Convenios 94:16',
